import { render as Label } from '../label/label.js';
import { classes } from "./button.st.css.js";

const render = (text = '') => {
  const btn = document.createElement("button");
  const label = Label(text);
  btn.appendChild(label);
  btn.classList.add(classes.root);
  label.classList.add(classes.text);
  return btn;
};

export { classes, render };
